import React, {useState, useEffect} from 'react';
import "./App.scss";
import Number from "../Number";
import {generateCells} from "../../utils";
import Button from '../Button';
import {face} from "../../types";

const App: React.FC = () =>{
    const[cells, setCells] = useState(generateCells());
    const[Face, setFace] = useState(face.lost);

    useEffect(() =>{
        const handleKeydown = () =>{
            setFace(face.lost);
        }
        window.addEventListener('keydown', handleKeydown)
    }, [])
    console.log(cells); 
    const renderCells = ():React.ReactNode => {
        return cells.map((i, rowIndex) => i.map((cell, colIndex) => 
        <Button key={`${rowIndex}-${colIndex}`} row={rowIndex} col={colIndex} state={cell.state} value={cell.value}/>)
        );
    };
    return(
        <div className="App">
            <div className="preHeader">
                <p>Minesweeper</p>
                <p>Built By Hekmat</p>
            </div>
            <div className="header">
                <Number value={0}>

                </Number>
                <div className="face"> 
                    <span aria-label="face" role="img">Face</span> 
                </div>
                <Number value={23}>

                </Number>
            </div>
            <div className="body">
                {renderCells()}
            </div>


        </div>

    )
};
export default App;
